//! Compiled wallet contract codes (hex)

use std::sync::Arc;
use ton_cell::{BagOfCells, Cell, CellResult};

/// Wallet V3R2 code (hex)
const WALLET_V3R2_CODE: &str = "B5EE9C724101010100710000DEFF0020DD2082014C97BA218201339CBAB19F71B0ED44D0D31FD31F31D70BFFE304E0A4F2608308D71820D31FD31FD31FF82313BBF263ED44D0D31FD31FD3FFD15132BAF2A15144BAF2A204F901541055F910F2A3F8009320D74A96D307D402FB00E8D101A4C8CB1FCB1FCBFFC9ED5410BD6DAD";

/// Wallet V4R2 code (hex)
const WALLET_V4R2_CODE: &str = "";

/// Wallet V5R1 code (hex) - from tonutils-go
const WALLET_V5R1_CODE: &str = "";

/// Highload Wallet V2R2 code (hex)
const HIGHLOAD_V2R2_CODE: &str = "";

/// Highload Wallet V3 code (hex) - same as V2R2 for now
const HIGHLOAD_V3_CODE: &str = "";

/// Get wallet V3R2 code cell
pub fn wallet_v3r2_code() -> CellResult<Arc<Cell>> {
    let boc = BagOfCells::deserialize_from_hex(WALLET_V3R2_CODE)?;
    boc.single_root().cloned()
}

/// Get wallet V4R2 code cell
pub fn wallet_v4r2_code() -> CellResult<Arc<Cell>> {
    let boc = BagOfCells::deserialize_from_hex(WALLET_V4R2_CODE)?;
    boc.single_root().cloned()
}

/// Get wallet V5R1 code cell
pub fn wallet_v5r1_code() -> CellResult<Arc<Cell>> {
    let boc = BagOfCells::deserialize_from_hex(WALLET_V5R1_CODE)?;
    boc.single_root().cloned()
}

/// Get highload V2R2 code cell
pub fn highload_v2r2_code() -> CellResult<Arc<Cell>> {
    let boc = BagOfCells::deserialize_from_hex(HIGHLOAD_V2R2_CODE)?;
    boc.single_root().cloned()
}

/// Get highload V3 code cell
pub fn highload_v3_code() -> CellResult<Arc<Cell>> {
    let boc = BagOfCells::deserialize_from_hex(HIGHLOAD_V3_CODE)?;
    boc.single_root().cloned()
}
